mapper.update:
update freelistenbook set
status = #{status}
where id =
#{id}

mapper.select:
select freelistenbook.id,freelisten.title,freelistenbook.username,freelistenbook.tel,freelistenbook.status,freelistenbook.booktime,freelistenbook.comment from freelistenbook,freelisten where freelistenbook.fid=freelisten.id and freelistenbook.fid in ( select id from freelisten where qid = #{qid})
and freelistenbook.id = #{id}
and freelistenbook.status = #{status}
and unix_timestamp(freelistenbook.booktime) > unix_timestamp(#{starttime})
and unix_timestamp(freelistenbook.booktime) < unix_timestamp(#{endtime})
