mapper.update:
update refund set
status = '已处理'
where oid = #{oid}

mapper.select:
select * from refund where oid in (select oid from sorder where qid = #{qid})
and oid = #{oid}
and status = #{status}
and unix_timestamp(refundtime) > unix_timestamp(#{starttime})
and unix_timestamp(refundtime) < unix_timestamp(#{endtime})

select sorder.lid,sorder.oid,sorder.nickname,sorder.tel,sorder.total,sorder.cid,sorder.actual,sorder.status,sorder.ordertime from sorder,refund
where sorder.oid=refund.oid
and sorder.qid=#{qid}
